    //     // Redirect to student page if login is successful

    //   })
    //   .catch(error => {
    //     console.error('Error:', error);
    //   });
    // This could be part of your login function in a React component
fetch('/api/login', {
  method: 'POST',
  headers: {
      'Content-Type': 'application/json',
  },
  body: JSON.stringify({ userId: 'yourUserId' }),
})
.then(response => response.json())
.then(data => {
  if (data.redirect) {
      // Use react-router-dom's useHistory hook for navigation
      history.push(data.redirect);
  }
})
.catch((error) => {
  console.error('Error:', error);
});

  }

  return (
    <div className="heading">
      <div className="title">
        <span>Student Login</span>
      </div>
      <form onSubmit={handleForm}>
        <div className="row">
          <i className="fa-regular fa-heart" />
          <input type="text" name="userId" placeholder="Email or Phone" required="" onChange={handleChange} />
        </div>
        <div className="row">
          <i className="fa-solid fa-heart" />
          <input type="password" name="password" placeholder="Password" required="" onChange={handleChange} />
        </div>
        <div className="pass">
          <a href="#">Forgot password?</a>
        </div>
        <div className="row button">
          <input type="submit" value="Login" />
        </div>
        <div className="signup-link">
          Not a member? <Link to="/signup">Signup Now</Link>
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
